import { observable, action } from "mobx";
import axios from "axios";

export class admin {
  @observable dbTable = {};
  @observable dbName = "";
  @observable subtitle = "";
  @observable contents = "";
  @observable num = 0;
  @observable tableLength = 0;
  @action
  createTableInfo() {
    console.log();
    return axios
      .get("https://hanstream.gabia.io/adminCreateFile", {
        params: {
          dbName: this.dbName,
        },
      })
      .then((response) => {
        alert("추가완료했습니다.");
        console.log(response.data);
        this.insertTable();
      })
      .catch((error) => {
        alert("error");
        console.log(error);
      });
  }
  @action
  async createTable() {
    await this.createTableInfo();
  }

  @action
  insertTableInfo() {
    return axios
      .get("https://hanstream.gabia.io/insertDBValue", {
        params: {
          num: this.num,
          name: this.dbName,
        },
      })
      .then((response) => {
        console.log(response.data);
        this.getTableLength();
      })
      .catch((error) => {
        console.log(error);
      });
  }
  @action
  async insertTable() {
    await this.insertTableInfo();
  }

  @action
  getTableLengthInfo() {
    return axios
      .get("https://hanstream.gabia.io/getTableLength", {
        params: {
          dbName: this.dbName,
        },
      })
      .then((response) => {
        console.log(response.data);
        if (response.data === null) {
          this.tableLength = 0;
        }
        alert(this.tableLength);
        this.insertValue();
      })
      .catch((error) => {
        console.log(error);
      });
  }
  @action
  async getTableLength() {
    await this.getTableLengthInfo();
  }

  @action
  insertValueInfo() {
    return axios
      .get("https://hanstream.gabia.io/insertValue", {
        params: {
          num: this.tableLength + 1,
          dbName: this.dbName,
          subtitle: this.subtitle,
          contents: this.contents,
        },
      })
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  @action
  async insertValue() {
    await this.insertValueInfo();
  }

  @action
  getTableInfo() {
    return axios
      .get("https://hanstream.gabia.io/adminDBTable", {})
      .then((response) => {
        console.log(response.data);
        this.dbTable = response.data;
      })
      .catch((error) => {
        console.log(error);
      });
  }
  @action
  async getTable() {
    await this.getTableInfo();
  }
}
