    margin-top: 10%;
    margin-bottom: 10%;
  `;

  const ButtonDesign = styled.button`
    width: 100%;
    margin-bottom: -1%;
    background-color: #00d3ee;
    border: none;
    color: white;
    padding: 15px 32px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 16pt;
    border-radius: 5px;
  `;

  const ButtonList = styled.button`
    width: 100%;
    margin-bottom: 5%;
    background-color: #c4c4c4;
    border: none;
    color: white;
    padding: 15px 32px;
