import React, { useState, useContext, useEffect } from "react";
import { adminContext } from "../../context/admin";
import { observer } from "mobx-react-lite";
const createTable = observer(() => {
  const adminInfo = useContext(adminContext);
  useEffect(() => {
    adminInfo.getTable();
    console.log(adminInfo.dbTable);
  }, []);
  const [dbName, setDBName] = useState();
  const handleChangeName = (e) => {
    setDBName(e.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    adminInfo.createTable(dbName);
  };
  return (
    <div>
      {adminInfo.dbTable.map((contents, num) => (
        <>
          title={contents.listtitle}
          num={contents.num}
          <br />
        </>
      ))}
      <form onSubmit={handleSubmit}>
        <input type="text" value={dbName} onChange={handleChangeName} />
        <button type="submit">테이블만들기</button>
      </form>
    </div>
  );
});

export default createTable;
