import React, { useContext, useEffect } from "react";
import {
  Col,
  Row,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Card,
  CardImg,
  CustomInput,
} from "reactstrap";
import { observer } from "mobx-react-lite";
//import { withRouter } from "react-router-dom";
import BirthImage from "./Birth.png";
import styled from "styled-components";
import { birthContext } from "../context/birth";
import { signinUpContext } from "../context/signinup";
import DayForm from "../component/birth/birthform_day";
import MonForm from "../component/birth/birthform_mon";
import YearForm from "../component/birth/birthform_year";
import axios from "axios";
import { useRouter } from "next/router";
import Birth from "../component/birth/birth";
import Router from "next/router";
import ReactPixel from "react-facebook-pixel";
import { IoLogoOctocat } from "react-icons/io";
import MoreButton from "../component/signinList";

const birthform = observer(() => {
  const birthInfo = useContext(birthContext);
  const signInfo = useContext(signinUpContext);
  const router = useRouter();
  // eslint-disable-next-line react-hooks/rules-of-hooks
  //const { birthTotal, handleSubmit } = useForm();
  /* useEffect(() => {
    const options = {
      autoConfig: true, // set pixel's autoConfig
      debug: false, // enable logs
    };
    ReactPixel.init("712715535735010", options);
    ReactPixel.track(props.data[0].title);
  }, []);*/
  const CardPosition = styled(Card)`
    margin-top: 10%;
    margin-bottom: 10%;
  `;

  const ButtonDesign = styled.button`
    width: 100%;
    margin-bottom: 3%;
    background-color: #00d3ee;
    border: none;
    color: white;
    padding: 15px 32px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 16pt;
    border-radius: 5px;
  `;

  const ButtonList = styled.button`
    width: 100%;
    margin-bottom: 5%;
    background-color: #c4c4c4;
    border: none;
    color: white;
    padding: 15px 32px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 16pt;
    border-radius: 5px;
    font-weight: bold;
  `;

  const FormPosition1 = styled(Row)`
    width: 100%;
    margin-bottom: 5%;
    margin-top: 5%;
  `;

  const FormNotice = styled.div`
    width: 100%;
    margin-top: 10%;
    font-size: 16pt;
  `;
  const FormPosition2 = styled.div`
    margin-bottom: 10%;
  `;

  const FormPosition3 = styled.div`
    width: 92%;
    margin-top: 5%;
    margin-bottom: 5%;
    margin-left: 4%;
  `;

  const SelectButton = styled.div`
    padding-top: 1%;
  `;
  const Highlight = styled.span`
    color: red;
  `;

  const handleChangeSolarLunar = (e) => {
    birthInfo.birth_SolarTrueFalse = !birthInfo.birth_SolarTrueFalse;
    birthInfo.birth_LunarTrueFalse = !birthInfo.birth_LunarTrueFalse;
  };
  const handleChangeTime = (e) => {
    birthInfo.birth_time = e.target.value;
  };
  const handleChangeLeapMonth = (e) => {
    birthInfo.birth_leap_month = !birthInfo.birth_leap_month;
  };
  const handleChangeGender = (e) => {
    birthInfo.gender = e.target.value;
    console.log(birthInfo.gender);
  };
  const handleChangeEmail = (e) => {
    signInfo.email = e.target.value;
    console.log(signInfo.email);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      birthInfo.checkYearError === true ||
      birthInfo.checkMonError === true ||
      birthInfo.checkDayError === true
    ) {
      alert("생년월일시를 다시확인해주세요!");
      console.log(birthInfo.checkYearError);
      console.log(birthInfo.checkMonError);
      console.log(birthInfo.checkDayError);
    } else {
      alert("good");
    }
  };

  return (
    <Birth>
      <Form action="/result" onSubmit={handleSubmit}>
        <FormPosition1 form>
          <Col md="3">
            <YearForm />
          </Col>

          <Col xs="3">
            <MonForm />
          </Col>
          <Col xs="3">
            <DayForm />
          </Col>
          <Col xs="3">
            <FormGroup>
              <Label for="examplePassword">시간</Label>
              <Input
                type="select"
                name="password"
                id="examplePassword"
                placeholder="password placeholder"
                onChange={handleChangeTime}
              >
                <option value="0">시간 모름</option>
                <option value="1">23:30~01:30 子시(자시)</option>
                <option value="2">01:30~03:30 丑시(축시)</option>
                <option value="3">03:30~05:30 寅시(인시)</option>
                <option value="4">05:30~07:30 卯시(묘시)</option>
                <option value="5">07:30~09:30 辰시(진시)</option>
                <option value="6">09:30~11:30 巳시(사시)</option>
                <option value="7">11:30~13:30 午시(오시)</option>
                <option value="8">13:30~15:30 未시(미시)</option>
                <option value="9">15:30~17:30 申시(신시)</option>
                <option value="10">17:30~19:30 酉시(유시)</option>
                <option value="11">19:30~21:30 戌시(술시)</option>
                <option value="12">21:30~23:30 亥시(해시)</option>
              </Input>
            </FormGroup>
          </Col>

          {/*  <FormGroup>
            <SelectButton>
              <CustomInput
                type="radio"
                id="gender1"
                name="gender"
                label="여자"
                inline
                value="female"
                checked={birthInfo.gender_femaleTrueFalse}
                onChange={handleChangeGender}
              />
              <CustomInput
                type="radio"
                id="gender2"
                name="gender"
                label="남자"
                inline
                value="male"
                checked={birthInfo.gender_maleTrueFalse}
                onChange={handleChangeGender}
              />
            </SelectButton>
          </FormGroup>*/}
          {/*   <Col md={3} xs={12}>
            <FormGroup>
              <Label for="examplePassword">시간</Label>
              <Input
                type="select"
                name="password"
                id="examplePassword"
                placeholder="password placeholder"
                onChange={handleChangeTime}
              >
                <option value="0">시간 모름</option>
                <option value="1">23:30~01:30 子시(자시)</option>
                <option value="2">01:30~03:30 丑시(축시)</option>
                <option value="3">03:30~05:30 寅시(인시)</option>
                <option value="4">05:30~07:30 卯시(묘시)</option>
                <option value="5">07:30~09:30 辰시(진시)</option>
                <option value="6">09:30~11:30 巳시(사시)</option>
                <option value="7">11:30~13:30 午시(오시)</option>
                <option value="8">13:30~15:30 未시(미시)</option>
                <option value="9">15:30~17:30 申시(신시)</option>
                <option value="10">17:30~19:30 酉시(유시)</option>
                <option value="11">19:30~21:30 戌시(술시)</option>
                <option value="12">21:30~23:30 亥시(해시)</option>
              </Input>
            </FormGroup>
          </Col>*/}
        </FormPosition1>

        <Row>
          <Col md={6} xs="12">
            <FormGroup>
              <SelectButton>
                <CustomInput
                  type="radio"
                  id="exampleCustomRadio"
                  name="yangumm"
                  label="양력"
                  inline
                  value="solar"
                  checked={birthInfo.birth_SolarTrueFalse}
                  onChange={handleChangeSolarLunar}
                />
                <CustomInput
                  type="radio"
                  id="exampleCustomRadio2"
                  name="yangumm"
                  label="음력"
                  inline
                  value="Lunar"
                  checked={birthInfo.birth_LunarTrueFalse}
                  onChange={handleChangeSolarLunar}
                />
                <CustomInput
                  type="checkbox"
                  id="exampleCustomInline"
                  label="윤달"
                  inline
                  checked={birthInfo.birth_leap_month}
                  onChange={handleChangeLeapMonth}
                />
                <CustomInput
                  type="radio"
                  id="gender1"
