import React, { useState, useContext, useEffect } from "react";
import { adminContext } from "../../context/admin";
const showContents = (number) => {
  const [subtitle, setSubTitle] = useState();
  const adminInfo = useContext(adminContext);
  useEffect(() => {
    adminInfo.updateSubtitle = number.subtitle;
    adminInfo.updateContents = number.contents;
    console.log(adminInfo.dbTable);
  }, []);
  console.log(number);
  const handleUpdateTitle = (e) => {
    number.number = e.target.value;
  };
  const handleUpdateContents = (e) => {
    number.subtitle = e.target.value;
  };
  return (
    <div>
      {number.number} <br />
      {number.subtitle} <br />
      {number.contents}
      <form>
        <br />
        제목{" "}
        <input type="text" value={number.number} onChange={handleUpdateTitle} />
        <br />
        내용
        <input
          type="text"
          value={number.subtitle}
          onChange={handleUpdateContents}
        />
      </form>
    </div>
  );
};

export default showContents;
