/* eslint-disable react/jsx-no-comment-textnodes */
import React, { useState, useContext } from "react";
import {
  CardImg,
  CardTitle,
  CardSubtitle,
  CardBody,
  CardText,
  Row,
  Col,
  Card,
} from "reactstrap";
import styled from "styled-components";
import Link from "next/link";
import { birthContext } from "../../context/birth";
import { observer } from "mobx-react-lite";
import { signinUpContext } from "../../context/signinup";
const cardlist = observer((title, index, props) => {
  console.log(index);
  console.log(title.image);
  const birthInfo = useContext(birthContext);
  const signInfo = useContext(signinUpContext);

  const inputBirthNumber = (num) => {
    //console.log(title.num);
    //console.log(key);
    //let a = title.num;

    const d = new Date();
    let resultLink =
      "/result?month=" +
      birthInfo.birth_month +
      "&day=" +
      birthInfo.birth_day +
      "&dbname" +
      birthInfo.dbName;
    console.log(d);
    birthInfo.postNumber = num;
    birthInfo.dbContentsName = title.title;
    console.log(birthInfo.postNumber);
    console.log(title.num);
    birthInfo.title = "";
    birthInfo.subtitle = "";
    birthInfo.contents = "";
    birthInfo.birth_year = signInfo.memberYear;
    birthInfo.birth_month = signInfo.memberMon;
    birthInfo.birth_day = signInfo.memberDay;
    birthInfo.getContentsSearch();
    // setLoading(true);
  };
  const CardTitleSize = styled(CardTitle)`
    font-size: 1.2em;
    font-weight: bold;
    margin-bottom: -1%;
  `;
  const LinkFontStyle = styled(Link)`
    color: black;
    text-decoration: none;
    &:hover {
      color: black;
      text-decoration: none !important;
    }
  `;
  //console.log(contents.contents.num);
  //console.log(contents.contents.title);
  console.log(title.title);
  console.log(title.num);
  // console.log(key);
  const pleaseLogin = () => {
    alert("로그인을 해야 이용가능한 서비스입니다.");
  };
  return (
    <div>
      {/*첫번째 요소*/}

      <div>
        {signInfo.kakaoId === "" ? (
          <>
            <Card
              inverse
              style={{ backgroundColor: "#00D3EE", border: "0px" }}
              onClick={pleaseLogin}
            >
              <CardImg
                top
                width="100%"
                src={title.image}
                alt="Card image cap"
              />
              <CardBody>
                <CardTitleSize>{title.title}</CardTitleSize>
              </CardBody>
            </Card>
            <br />
          </>
        ) : (
          <>
            <LinkFontStyle href={resultLink}>
              <Card
                inverse
                style={{ backgroundColor: "#00D3EE", border: "0px" }}
                onClick={() => inputBirthNumber(title.num)}
              >
                <CardImg
                  top
                  width="100%"
                  src={title.image}
                  alt="Card image cap"
                />
                <CardBody>
                  <CardTitleSize>{title.title}</CardTitleSize>
                </CardBody>
              </Card>
            </LinkFontStyle>
          </>
        )}
      </div>
    </div>
  );
});

export default cardlist;
