                />
              </SelectButton>
         </FormGroup>*/}
          </Col>

          <ButtonDesign color="info" size="lg" type="submit">
            {props.data[0].title}??
          </ButtonDesign>
        </Row>
      </Form>
      {/*<CardPosition>
        <CardImg
          top
          width="100%"
          src={"/image/notice.png"}
          alt="Card image cap"
        />
      </CardPosition>*/}
      <h2>이메일을 입력하면</h2>
      정식출시시 <span>유료테스트 무료1회!</span>
    </Birth>
  );
});

birthform.getInitialProps = async function ({ query }) {
  //const url = require("url");
  //console.log(url);
  //  console.log(query.id);
  const res = await axios.get("https://hanstream.gabia.io/searchSimpleList", {
    params: {
      num: query.num,
    },
  });
  const data = await res.data;

  console.log(data);

  console.log(`Show data fetched. Count: ${data.length}`);
  return {
    data: data,
  };
};
export default birthform;
