import React, { useState, useContext, useEffect } from "react";
import {
  CardImg,
  CardTitle,
  CardSubtitle,
  CardBody,
  CardText,
  Row,
  Col,
  Card,
  Container,
} from "reactstrap";
import { observer } from "mobx-react-lite";
import { birthContext } from "../context/birth";
import styled from "styled-components";
import Link from "next/link";
import axios from "axios";
import CardList from "../component/contents/simpleTestList";
import FirstImage from "../component/image";
const LinkFontStyle = styled(Link)`
  color: black;
  text-decoration: none;
  &:hover {
    color: black;
    text-decoration: none !important;
  }
`;
const CardTitleSize = styled(CardTitle)`
  font-size: 1.2em;
  font-weight: bold;
`;
const simple = observer(() => {
  const birthInfo = useContext(birthContext);

  const [loading, setLoading] = useState(true);
  const [contents, setContents] = useState([]);
  /*useEffect(() => {
    console.log(birthInfo.simpleTest);
    if (birthInfo.simpleTest.length === 0) {
      // birthInfo.ContentsTitle().then(setLoading(true));
      console.log(birthInfo.simpleTest[0]);

      axios
        .get(
          "https://hanstream.gabia.io/simpleTest",
          {},
          { withCredentials: true }
        )
        .then((response) => {
          console.log(response);
          for (let i = 0; i < response.data.length; i++) {
            let temp = {
              num: response.data[i].num,
              title: response.data[i].title,
              name: response.data[i].name,
              imgName: response.data[i].imgName,
            };
            birthInfo.simpleTest.push(temp);
          }
          console.log(birthInfo.simpleTest[0]);
          setContents(birthInfo.simpleTest);
          const interval = setTimeout(() => {
            setLoading(false);
          }, 3 * 1000);
          return () => {
            clearTimeout(interval);
          };
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      setContents(birthInfo.simpleTest);
      setLoading(false);
    }
    // eslint-disable-next-line
  }, []);*/
  const inputBirthNumber = (num) => {
    birthInfo.postNumber = birthInfo.contentsList[num - 1].num;
    console.log(num);
    birthInfo.title = "";
    birthInfo.subtitle = "";
    birthInfo.contents = "";
  };
  const ContentsBody = styled.div`
    width: 100%;
    display: flex;
    justify-content: center;
  `;
  return (
    <ContentsBody>
      <Container>
        <Row>
          <Col md="12" xs="12">
            <FirstImage />
            <hr />
            <Row>
              {props.data.map((contents, num) => (
                <>
                  <Col md="4" xs="12">
                    <CardList
                      title={contents.title}
                      num={contents.num}
                      name={contents.name}
                      image={contents.imageLink}
                      props={props}
                    />{" "}
                  </Col>{" "}
                </>
              ))}
            </Row>
          </Col>
        </Row>
      </Container>
    </ContentsBody>
  );
});
simple.getInitialProps = async function ({ query }) {
  //const url = require("url");
  //console.log(url);
  //  console.log(query.id);
  const res = await axios.get("https://hanstream.gabia.io/simpleTest", {});
  const data = await res.data;

  console.log(data);

  console.log(`Show data fetched. Count: ${data.length}`);
  return {
    data: data,
  };
};
export default simple;
