import React, { useContext, useEffect, useState } from "react";
import { Row, Col, CustomInput } from "reactstrap";
import { birthContext } from "../context/birth";
import Result from "../component/result/result";
import { useRouter } from "next/router";
import axios from "axios";
import { CopyToClipboard } from "react-copy-to-clipboard";
import TwitterImage from "./twitter.png";
import FacebookImage from "./facebook.png";
import KakaoImage from "./kakaotalk.png";
import LinkImage from "./link.png";
import styles from "../component/result.module.css";
import ShareButton from "../component/result/shareButton";

const resultContents = (props) => {
  let testValue = 0;
  const BirthInfo = useContext(birthContext);
  const router = useRouter();
  const [loading, setLoading] = useState(true);
  const [loadingTime, setLoadingTime] = useState(0);
  const [countNumber, setCountNumber] = useState(0);

  //초기설정
  useEffect(() => {
    setCountNumber(props.data[0].goodButton);
    BirthInfo.postGoodOrNot = false;
    const interval = setInterval(() => {
      testValue++;
      setLoadingTime(testValue);
      console.log(loadingTime);
      console.log(testValue);
    }, 380);
    setTimeout(() => {
      setLoading(false);
      clearInterval(interval);
    }, 3800);
  }, []);
  //조아요 버튼 눌렀을때
  const handleChangeGoodOrNot = (e) => {
    BirthInfo.postGoodOrNot = !BirthInfo.postGoodOrNot;
    let value;
    if (BirthInfo.postGoodOrNot === true) {
      value = countNumber + 1;
    } else {
      value = countNumber - 1;
    }
    axios
      .get("https://hanstream.gabia.io/goodCount", {
        params: {
          count: value,
          dbName: router.query.dbname,
        },
      })
      .then((response) => {
        console.log(response);
        setCountNumber(value);
        console.log(countNumber);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  if (loading)
    return (
      <div>
        <Row>
          <Col md="3"></Col>
          <Col md="6" xs="12">
            <h3>일부러 안보이는척 하는중...</h3>
            <img src={"/image/loading.gif"} class="card-img-top" alt="..." />
          </Col>
          <Col md="3"></Col>
        </Row>
      </div>
    );

  //Layout
  return (
    <Result>
      <div className={styles.card}>
        <div class="card">
          <img
            src={
              "/image/" +
              router.query.dbname +
              "/" +
              props.data[0].title +
              ".png"
            }
            class="card-img-top"
            alt="..."
          />
        </div>

        <hr />
        <div className={styles.MoreInfoText}> 복채는 하트와 공유로! </div>
        <ShareButton
          num={props.data[0].num.num}
          dbname={router.query.dbname}
          countNumber={countNumber}
        />
        {/*좋아요버튼 나중에 수정할 예정. */}
        <CustomInput
          type="switch"
          id="exampleCustomSwitch"
          name="customSwitch"
          label={countNumber + "명이 이테스트를 좋아합니다"}
          onChange={handleChangeGoodOrNot}
        />
        {/*타이틀 */}
        <div className={styles.TitlePosition}>{props.data[0].subtitle}</div>
        {/*내용 */}
        {props.data[0].contents.split("\n").map((line) => {
          return (
            <span>
              {line}
              <br />
            </span>
          );
        })}
      </div>
      {/*signInfo.kakaoId == "" && signInfo.email == "" ? <SignIn /> : <></>*/}
      {/*<hr />
      <MoreInfoText> 더 많은 테스트 보기</MoreInfoText>*/}
    </Result>
  );
};
resultContents.getInitialProps = async function ({ query }) {
  const res = await axios.get("https://hanstream.gabia.io/birth", {
    params: {
      month: query.month,
      day: query.day,
      dbName: query.dbname,
    },
  });
  const data = await res.data;

  console.log(data);

  console.log(`Show data fetched. Count: ${data.length}`);
  return {
    data: data,
  };
};
export default resultContents;
