import React, { useContext } from "react";
import {
  Col,
  Row,
  Form,
  FormGroup,
  Label,
  Input,
  Card,
  CardImg,
} from "reactstrap";
import { observer } from "mobx-react-lite";
//import { withRouter } from "react-router-dom";
import BirthImage from "./Birth.png";
import styled from "styled-components";
import { birthContext } from "../context/birth";
import { signinUpContext } from "../context/signinup";
import DayForm from "../component/birth/birthform_day";
import MonForm from "../component/birth/birthform_mon";
import { useRouter } from "next/router";
import Birth from "../component/birth/birth";
import Router from "next/router";

const birthform = observer((props) => {
  const birthInfo = useContext(birthContext);
  const signInfo = useContext(signinUpContext);
  const router = useRouter();
  const CardPosition = styled(Card)`
    margin-top: 10%;
    margin-bottom: 10%;
  `;

  const ButtonDesign = styled.button`
    width: 100%;
    margin-bottom: 3%;
    background-color: #00d3ee;
    border: none;
    color: white;
    padding: 15px 32px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 16pt;
    border-radius: 5px;
  `;

  const FormPosition1 = styled(Row)`
    width: 100%;
    margin-bottom: 5%;
    margin-top: 5%;
  `;

  const FormPosition3 = styled.div`
    width: 92%;
    margin-top: 5%;
    margin-bottom: 10%;
    margin-left: 4%;
  `;

  const handleChangeGender = (e) => {
    /* birthInfo.gender_femaleTrueFalse = !birthInfo.gender_femaleTrueFalse;
    birthInfo.gender_maleTrueFalse = !birthInfo.gender_maleTrueFalse;*/
    birthInfo.gender = e.target.value;
    console.log(birthInfo.gender);
  };
  const handleChangeEmail = (e) => {
    signInfo.email = e.target.value;
    console.log(signInfo.email);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    //  ReactPixel.track(props.data[0].title + "테스트결과를 보려고 하는중");

    if (
      //  birthInfo.checkYearError === true ||
      birthInfo.checkMonError === true ||
      birthInfo.checkDayError === true
    ) {
      alert("생년월일시를 다시확인해주세요!");
      console.log(birthInfo.checkYearError);
      console.log(birthInfo.checkMonError);
      console.log(birthInfo.checkDayError);
    } else {
      birthInfo.dbName = props.data[0].name;
      birthInfo.birth_year = 1800;
      birthInfo.birth_time = 0;
      let gender;
      gender = birthInfo.gender;
      /* if (birthInfo.gender_femaleTrueFalse) {
        gender = "female";
      } else {
        gender = "male";
      }*/
      /* if (signInfo.email === "") {
        birthInfo.simpleResult();
        Router.push(
          "/result?month=" +
            birthInfo.birth_month +
            "&day=" +
            birthInfo.birth_day +
            "&dbname=" +
            birthInfo.dbName
        );
      } else {
        birthInfo
          .simpleResult()
          .then((response) => {
            signInfo
              .getBirthInputPerson(
                gender,
                birthInfo.birth_year,
                birthInfo.birth_month,
                birthInfo.birth_day,
                birthInfo.birth_time
              )
              .then((response) => {
                Router.push(
                  "/result?month=" +
                    birthInfo.birth_month +
                    "&day=" +
                    birthInfo.birth_day +
                    "&dbname=" +
                    birthInfo.dbName
                );
              })
              .catch((error) => {
                console.log(error);
              });
          })
          .catch((error) => {
            console.log(error);
          });
      }*/
      birthInfo
        .simpleResult()
        .then((response) => {
          signInfo
            .getBirthInputPerson(
              gender,
              birthInfo.birth_year,
              birthInfo.birth_month,
              birthInfo.birth_day,
              birthInfo.birth_time
            )
            .then((response) => {
              Router.push(
                "/result?month=" +
                  birthInfo.birth_month +
                  "&day=" +
                  birthInfo.birth_day +
                  "&dbname=" +
                  birthInfo.dbName
              );
            })
            .catch((error) => {
              console.log(error);
            });
        })
        .catch((error) => {
          console.log(error);
        });
      /*  .then((response) => {
          birthInfo.name = "임시";
          signInfo
            .getJoinInfo(
              gender,
              birthInfo.birth_year,
              birthInfo.birth_month,
              birthInfo.birth_day,
              birthInfo.birth_time,
              birthInfo.name
            )
            .then((response) => {
              Router.push(
                "/result?month=" +
                  birthInfo.birth_month +
                  "&day=" +
                  birthInfo.birth_day +
                  "&dbname=" +
                  birthInfo.dbName
              );
            })
            .catch((error) => {
              console.log(error);
            });
        })
        .catch((error) => {
          console.log(error);
        });*/
    }

    // birthInfo.BirthResult(props);
    // birthInfo.birth_result = "자축월";
  };

  return (
    <Birth>
      {console.log(birthInfo.postNumber)}
      {router.query.num == 7 ||
      router.query.num == 1 ||
      router.query.num == 6 ? (
        <>
          <CardPosition>
            <CardImg
              top
              width="100%"
              src={"/image/" + props.data[0].name + "_1.png"}
              alt="Card image cap"
            />
          </CardPosition>
        </>
      ) : (
        <>
          <CardPosition>
            <CardImg
              top
              width="100%"
              src={"/image/" + props.data[0].name + ".png"}
              alt="Card image cap"
            />
          </CardPosition>
        </>
      )}

      <Form action="/result" onSubmit={handleSubmit}>
        <FormPosition1 form>
          {/*<Col md={3} xs={4}>
            <YearForm />
        </Col>*/}

          <Col xs="4">
            <MonForm />
          </Col>
          <Col xs="4">
            <DayForm />
          </Col>
          <Col xs="4">
            {/*<DayForm />*/}
            <FormGroup>
              <Label for="gender">성별</Label>
              <Input
                type="select"
                name="gender"
                id="gender"
                defaultValue={birthInfo.gender}
                onChange={handleChangeGender}
              >
                <option value="female">여자</option>
                <option value="male">남자</option>
              </Input>
            </FormGroup>
          </Col>
        </FormPosition1>

        <FormPosition3>
          <ButtonDesign type="submit"> {props.data[0].title}??</ButtonDesign>
        </FormPosition3>
      </Form>
    </Birth>
  );
});

birthform.getInitialProps = async function ({ query }) {
  //const url = require("url");
  //console.log(url);
  //  console.log(query.id);
  const res = await axios.get("https://hanstream.gabia.io/searchSimpleList", {
    params: {
      num: query.num,
    },
  });
  const data = await res.data;

  console.log(data);

  console.log(`Show data fetched. Count: ${data.length}`);
  return {
    data: data,
  };
};
export default birthform;
//export default withRouter(birthform);
