import React, { useContext, useEffect, useState } from "react";
import {
  Card,
  CardImg,
  Row,
  Col,
  Button,
  Popover,
  PopoverHeader,
  PopoverBody,
  Progress,
  CustomInput,
} from "reactstrap";
//import ResultImage from "./TestName_1.png";
import styled from "styled-components";
import GoodButton from "../component/goodButton";

import { birthContext } from "../context/birth";
import { signinUpContext } from "../context/signinup";
import Result from "../component/result";
import { useRouter } from "next/router";
import axios from "axios";
import { CopyToClipboard } from "react-copy-to-clipboard";
import TwitterImage from "./twitter.png";
import FacebookImage from "./facebook.png";
import KakaoImage from "./kakaotalk.png";
import LinkImage from "./link.png";
import SignIn from "../component/signin";
import ReactPixel from "react-facebook-pixel";
import ReactGA from "react-ga";
import TagManager from "react-gtm-module";

const resultContents = (props) => {
  let testValue = 0;
  const BirthInfo = useContext(birthContext);
  const signInfo = useContext(signinUpContext);
  const router = useRouter();
  const [popoverOpen, setPopoverOpen] = useState(false);
  const [loading, setLoading] = useState(true);
  const [loadingTime, setLoadingTime] = useState(0);
  const [countNumber, setCountNumber] = useState(0);
  const postLink = "https://sajubaju.com/birth?num=" + props.data[0].num;
  // const postLink = "https://sajubaju.lukawitch.now.sh" + router.asPath;
  const twitterLink = "https://twitter.com/intent/tweet?url=" + postLink;
  const facebookLink =
    "http://www.facebook.com/sharer/sharer.php?u=" + postLink;
  const imageLink =
    "https://sajubaju.com/image/" + router.query.dbname + ".png";
  useEffect(() => {
    console.log(router);
    setCountNumber(props.data[0].goodButton);
    BirthInfo.postGoodOrNot = false;
    const options = {
      autoConfig: true, // set pixel's autoConfig
      debug: false, // enable logs
    };
    ReactPixel.init("712715535735010", options);
    ReactGA.initialize("UA-167569535-1");
    // ReactGA.pageview(window.location.pathname + window.location.search);
    const tagManagerArgs = {
      gtmId: "GTM-MGN9D85",
    };

    TagManager.initialize(tagManagerArgs);
    window.Kakao.init("79f4a4e9c21f0d6f7f2a173a3c9e130f");
    const interval = setInterval(() => {
      testValue++;
      setLoadingTime(testValue);
      console.log(loadingTime);
      console.log(testValue);
    }, 380);
    setTimeout(() => {
      setLoading(false);
      //  BirthInfo.getContentsSearch();
      clearInterval(interval);
    }, 3800);
  }, []);
  const toggle = () => setPopoverOpen(!popoverOpen);
  const TitlePosition = styled.div`
    font-size: 1.8em;
    margin: 5% 0;
    font-weight: bold;
  `;
  const ResultContents = styled.div`
    font-size: 110%;
  `;

  const MoreInfoText = styled.div`
    font-size: 1.8em;
    font-weight: bold;
    text-align: center;
    margin-bottom: 5%;
  `;
  const handleChangeGoodOrNot = (e) => {
    BirthInfo.postGoodOrNot = !BirthInfo.postGoodOrNot;
    alert(BirthInfo.postGoodOrNot);
    console.log(BirthInfo.dbName);
    let value;
    if (BirthInfo.postGoodOrNot === true) {
      value = countNumber + 1;
    } else {
      value = countNumber - 1;
    }
    axios
      .get("https://hanstream.gabia.io/goodCount", {
        params: {
          count: value,
          dbName: router.query.dbname,
        },
      })
      .then((response) => {
        console.log(response);
        setCountNumber(value);
        console.log(countNumber);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const copyAlert = () => {
    alert("링크복사가 완료되었습니다");
    ReactPixel.track("Share");
  };
  const kakaoSharePixel = () => {};
  const twitterSharePixel = () => {
    ReactPixel.track("twitterShare");
  };
  const facebookSharePixel = () => {
    ReactPixel.track("facebookShare");
  };
  const LoadingPosition = styled.div`
    display: flex;
    align-content: center;
  `;
  if (loading)
    return (
      <div>
        <Row>
          <Col md="3"></Col>
          <Col md="6">
            <h3>일부러 안보이는척 하는중...</h3>
            {/*<Progress animated color="info" value={loadingTime} max={10} />*/}
            <img src={"/image/loading.gif"} class="card-img-top" alt="..." />
          </Col>
          <Col md="3"></Col>
        </Row>
      </div>
    );
  console.log(props.data[0].imageLink);
  const sendKakao = () => {
    console.log("ss");
    ReactPixel.track("kakaoShare");
    console.log(window.Kakao.Link.createDefaultButton);

    //
    window.Kakao.Link.createDefaultButton({
      container: "#kakao-link-btn",
      objectType: "feed",
      content: {
        title: props.data[0].listtitle,
        description: "더많은 테스트를보고싶으면",
        imageUrl: imageLink,
        link: {
          //webUrl: postLink,
          mobileWebUrl: postLink,
          androidExecParams: "test",
        },
      },

      buttons: [
        {
          title: "자세히보기",
          link: {
            //webUrl: postLink,
            mobileWebUrl: postLink,
          },
        },
      ],
      success: function (response) {
        console.log(response);
      },
      fail: function (error) {
        console.log(error);
      },
    });
  };
  return (
    <Result>
      <div class="row">
        <div class="col-md-3"></div>
        <div class="col-md-6">
          <div class="card">
            <img
              src={
                "/image/" +
                router.query.dbname +
                "/" +
                props.data[0].title +
                ".png"
              }
              class="card-img-top"
              alt="..."
            />
          </div>
        </div>
        <div class="col-md-3"></div>
      </div>
      <hr />
      <MoreInfoText> 복채는 하트와 공유로! </MoreInfoText>
      <Row>
        <Col md="4"></Col>
        <Col xs="3" md="1">
          <div id="kakao-link-btn" onClick={sendKakao}>
            <img src={KakaoImage} alt=".." class="card-img-top" />
          </div>
        </Col>
        <Col xs="3" md="1">
          <a href={twitterLink}>
            <img src={TwitterImage} alt=".." class="card-img-top" />
          </a>
        </Col>
        <Col xs="3" md="1">
          <a href={facebookLink}>
            {" "}
            <img src={FacebookImage} alt=".." class="card-img-top" />
          </a>
        </Col>
        <Col xs="3" md="1">
          <CopyToClipboard text={postLink}>
            <img
              src={LinkImage}
              alt=".."
              class="card-img-top"
              onClick={copyAlert}
            />
          </CopyToClipboard>
        </Col>
        <Col md="4"></Col>
      </Row>
      <br />
      <CustomInput
        type="switch"
        id="exampleCustomSwitch"
        name="customSwitch"
        label={countNumber + "명이 이테스트를 좋아합니다"}
        onChange={handleChangeGoodOrNot}
      />
      <TitlePosition>{props.data[0].subtitle}</TitlePosition>
      <ResultContents>{props.data[0].contents}</ResultContents>
      <hr />
      {signInfo.kakaoId == "" && signInfo.email == "" ? <SignIn /> : <></>}
      {/*<hr />
      <MoreInfoText> 더 많은 테스트 보기</MoreInfoText>*/}
    </Result>
  );
};
resultContents.getInitialProps = async function ({ query }) {
  //const url = require("url");
  //console.log(url);
  //  console.log(query.id);
  const res = await axios.get("https://hanstream.gabia.io/birth", {
    //const res = await axios.get("http://localhost:8001/resultValue", {
    params: {
      month: query.month,
      day: query.day,
      dbName: query.dbname,
    },
  });
  const data = await res.data;

  console.log(data);

  console.log(`Show data fetched. Count: ${data.length}`);
  return {
    data: data,
  };
};
export default resultContents;
