import React, { useContext, useEffect, useState } from "react";
import {
  Card,
  CardImg,
  Row,
  Col,
  Button,
  Popover,
  PopoverHeader,
  PopoverBody,
  Progress,
} from "reactstrap";
//import ResultImage from "./TestName_1.png";
import styled from "styled-components";
import CardList from "../contents/card";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import { Checkbox } from "@material-ui/core";
import Favorite from "@material-ui/icons/Favorite";
import FavoriteBorder from "@material-ui/icons/FavoriteBorder";
import { birthContext } from "../../../context/birth";
import { TwitterButton } from "react-social-kr";
import { FacebookProvider, Share } from "react-facebook";
import { signinUpContext } from "../../../context/signinup";
import { CopyToClipboard } from "react-copy-to-clipboard";
import { useRouter } from "next/router";

const resultContents = (props) => {
  let testValue = 0;
  const BirthInfo = useContext(birthContext);
  const signInfo = useContext(signinUpContext);
  const [popoverOpen, setPopoverOpen] = useState(false);
  const [loading, setLoading] = useState(true);
  const [loadingTime, setLoadingTime] = useState(0);
  useEffect(() => {
    console.log("sss");
    const interval = setInterval(() => {
      testValue++;
      setLoadingTime(testValue);
      console.log(loadingTime);
      console.log(testValue);
    }, 380);
    setTimeout(() => {
      setLoading(false);
      //  BirthInfo.getContentsSearch();
      clearInterval(interval);
    }, 3800);
  }, []);
  const toggle = () => setPopoverOpen(!popoverOpen);
  const TitlePosition = styled.div`
    font-size: 1.8em;
    margin: 5% 0;
    font-weight: bold;
  `;
  const ResultContents = styled.div`
    font-size: 110%;
  `;

  const MoreInfoText = styled.div`
    font-size: 1.8em;
    font-weight: bold;
    text-align: center;
    margin-bottom: 5%;
  `;
  if (loading)
    return (
      <div>
        일부러 안보이는척 하는중...
        <Progress animated color="info" value={loadingTime} max={10} />
      </div>
    );

  return (
    <div>
      <Row>
        <Col md="3"></Col>
        <Col md="6">
          <Card>
            <CardImg
              top
              width="100%"
              src={require("./" +
                router.query.dbName +
                "/" +
                props.data[0].title +
                ".png")}
              alt="Card image cap"
            />
          </Card>
        </Col>
        <Col md="3"></Col>
      </Row>
      <FormControlLabel
        control={
          <Checkbox
            icon={<FavoriteBorder />}
            checkedIcon={<Favorite />}
            name="checkedH"
          />
        }
        label={testValue}
      />

      <TitlePosition>{props.data[0].subtitle}</TitlePosition>
      <ResultContents>{props.data[0].contents}</ResultContents>
      <hr />
      <MoreInfoText> 복채는 하트와 공유로! </MoreInfoText>
      <FormControlLabel
        control={
          <Checkbox
            icon={<FavoriteBorder />}
            checkedIcon={<Favorite />}
            name="checkedH"
          />
        }
        label={testValue}
      />
      <hr />
      <MoreInfoText> 더 많은 테스트 보기</MoreInfoText>
      <CardList />
    </div>
  );
};
Search.getInitialProps = async function ({ query }) {
  //const url = require("url");
  //console.log(url);
  //  console.log(query.id);
  const res = await axios.get("https://hanstream.gabia.io/birth", {
    params: {
      month: query.month,
      day: query.day,
      dbName: query.dbname,
    },
  });
  const data = await res.data;

  console.log(data);

  console.log(`Show data fetched. Count: ${data.length}`);
  return {
    data: data,
  };
};
export default resultContents;
