import React, { useContext, useEffect, useState } from "react";
import { Button, Progress } from "reactstrap";
import Router from "next/router";
import { signinUpContext } from "../context/signinup";
import { observer } from "mobx-react-lite";
import TagManager from "react-gtm-module";
import ReactPixel from "react-facebook-pixel";
import styled from "styled-components";

const joinsuccess = observer(() => {
  let testValue = 0;
  const signInfo = useContext(signinUpContext);
  const [loading, setLoading] = useState(true);
  const [loadingTime, setLoadingTime] = useState(0);
  useEffect(() => {
    /*  const options = {
      autoConfig: true, // set pixel's autoConfig
      debug: false, // enable logs
    };
    ReactPixel.init("712715535735010", options);
    ReactPixel.track("최종회원가입완료");*/
    console.log("sss");
    const interval = setInterval(() => {
      testValue++;
      setLoadingTime(testValue);
      console.log(loadingTime);
      console.log(testValue);
    }, 200);
    setTimeout(() => {
      setLoading(false);
      //  BirthInfo.getContentsSearch();
      clearInterval(interval);
    }, 2000);
  }, []);
  const success = () => {
    signInfo.JoinOrNot = false;
    signInfo.successOrNot = false;
    signInfo.kakaoId = "";
    signInfo.memberYear = "";
    signInfo.memberMon = "";
    signInfo.memberDay = "";
    signInfo.name = "";
    signInfo.password = "";
    signInfo.email = "";
    Router.push("/");
  };
  const CenterPosition = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    width: 100vw;
    height: 100vh;
  `;
  if (loading)
    return (
      <div>
        Loading...
        <Progress animated color="info" value={loadingTime} max={10} />
      </div>
    );
  return (
    <CenterPosition>
      회원가입완료
      <br />
      <Button onClick={success}>홈으로 돌아가기</Button>
    </CenterPosition>
  );
});

export default joinsuccess;
