import React, { useContext, useEffect, useState } from "react";
import { Row, Col, CustomInput } from "reactstrap";
import { birthContext } from "../context/birth";
import Result from "../component/result";
import { useRouter } from "next/router";
import axios from "axios";
import { CopyToClipboard } from "react-copy-to-clipboard";
import TwitterImage from "./twitter.png";
import FacebookImage from "./facebook.png";
import KakaoImage from "./kakaotalk.png";
import LinkImage from "./link.png";
import styles from "../component/result.module.css";
import ShareButton from "../component/result/shareButton";

const shareButton = (num, dbname) => {
  //공유링크
  const postLink = "https://sajubaju.com/birth?num=" + num;
  const twitterLink = "https://twitter.com/intent/tweet?url=" + postLink;
  const facebookLink =
    "http://www.facebook.com/sharer/sharer.php?u=" + postLink;
  const imageLink = "https://sajubaju.com/image/" + dbname + ".png";
  //카카오톡 공유처리
  const sendKakao = () => {
    window.Kakao.Link.createDefaultButton({
      container: "#kakao-link-btn",
      objectType: "feed",
      content: {
        title: props.data[0].listtitle,
        description: "더많은 테스트를보고싶으면",
        imageUrl: imageLink,
        link: {
          //webUrl: postLink,
          mobileWebUrl: postLink,
          androidExecParams: "test",
        },
      },

      buttons: [
        {
          title: "자세히보기",
          link: {
            //webUrl: postLink,
            mobileWebUrl: postLink,
          },
        },
      ],
      success: function (response) {
        console.log(response);
      },
      fail: function (error) {
        console.log(error);
      },
    });
  };

  //조아요 버튼 눌렀을때
  const handleChangeGoodOrNot = (e) => {
    BirthInfo.postGoodOrNot = !BirthInfo.postGoodOrNot;
    let value;
    if (BirthInfo.postGoodOrNot === true) {
      value = countNumber + 1;
    } else {
      value = countNumber - 1;
    }
    axios
      .get("https://hanstream.gabia.io/goodCount", {
        params: {
          count: value,
          dbName: router.query.dbname,
        },
      })
      .then((response) => {
        console.log(response);
        setCountNumber(value);
        console.log(countNumber);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  return (
    <>
      {/*공유버튼 */}
      <Row>
        <Col md="2"></Col>
        <Col xs="3" md="2">
          <div id="kakao-link-btn" onClick={sendKakao}>
            {/*카카오톡버튼 */}
            <img src={KakaoImage} alt=".." class="card-img-top" />
          </div>
        </Col>
        <Col xs="3" md="2">
          <a href={twitterLink}>
            {/*트위터버튼 */}
            <img src={TwitterImage} alt=".." class="card-img-top" />
          </a>
        </Col>
        <Col xs="3" md="2">
          <a href={facebookLink}>
            {/*페이스북버튼 */}
            <img src={FacebookImage} alt=".." class="card-img-top" />
          </a>
        </Col>
        <Col xs="3" md="2">
          {/*그냥링크버튼 */}
          <CopyToClipboard text={postLink}>
            <img
              src={LinkImage}
              alt=".."
              class="card-img-top"
              onClick={copyAlert}
            />
          </CopyToClipboard>
        </Col>
        <Col md="2"></Col>
      </Row>
      <br />
      {/*좋아요버튼 나중에 수정할 예정. */}
      <CustomInput
        type="switch"
        id="exampleCustomSwitch"
        name="customSwitch"
        label={countNumber + "명이 이테스트를 좋아합니다"}
        onChange={handleChangeGoodOrNot}
      />
    </>
  );
};

export default shareButton;
